(()=>{document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll("pre > code").forEach(e=>{let o=e.parentElement;o.classList.add("relative");let t=document.createElement("button");t.className="copy-btn absolute top-2 right-2 px-2 py-1 bg-secondaryBg text-primaryText text-sm rounded hover:bg-divider transition",t.textContent="Copy",t.addEventListener("click",()=>{let n=e.textContent;navigator.clipboard.writeText(n).then(()=>{t.textContent="Copied!",setTimeout(()=>t.textContent="Copy",2e3)}).catch(c=>{console.error("Failed to copy: ",c)})}),o.appendChild(t)})});})();
